Complete the following NuSMV solving the task go straight at traffic light:
MODULE main
VAR
  Pedestrian : boolean;
  Opposite_Car : boolean;
  Green_Traffic_Light: boolean;
  Green_Left_Turn_Light: boolean;
  Stop_Sign: boolean;
  Car_From_Left: boolean;
  Car_From_Right: boolean;
  Action : {Stop, Move_forward, Turn_left, Turn_right};

ASSIGN
  init(Green_Traffic_Light) := FALSE;
  next(Green_Traffic_Light) :=
    case
      TRUE: {TRUE, FALSE};
    esac;

  init(Green_Left_Turn_Light) := FALSE;
  next(Green_Left_Turn_Light) :=
    case
      TRUE: {TRUE, FALSE};
    esac;

  init(Opposite_Car) := FALSE;
  next(Opposite_Car) :=
    case
      TRUE: {TRUE, FALSE};
    esac;

  init(Car_From_Left) := FALSE;
  next(Car_From_Left) :=
    case
      TRUE: {TRUE, FALSE};
    esac;

  init(Car_From_Right) := FALSE;
  next(Car_From_Right) :=
    case
      TRUE: {TRUE, FALSE};
    esac;

  init(Pedestrian) := FALSE;
  next(Pedestrian) :=
    case
      TRUE : {TRUE, FALSE};
    esac;

  init(Stop_Sign) := FALSE;
  next(Stop_Sign) :=
    case
      TRUE: {TRUE, FALSE};
    esac;

init(Action) := Stop;
next(Action) :=
  case
    -- If there is a green traffic light and no obstacles, move forward
    Green_Traffic_Light & !Pedestrian & !Opposite_Car & !Car_From_Left & !Car_From_Right & !Stop_Sign: Move_forward;

    -- If there is a pedestrian, stop
    Pedestrian: Stop;

    -- If there is a stop sign, stop
    Stop_Sign: Stop;

    -- If the traffic light is not green or there are obstacles, stop
    !Green_Traffic_Light | Opposite_Car | Car_From_Left | Car_From_Right: Stop;

    -- Default action is to stop
    TRUE: Stop;
  esac;

